<?phpRoute::group(['middleware' => 'web', 'prefix' => 'goods', 'namespace' => 'Modules\Goods\Http\Controllers'], function(){    Route::group(['middleware' => 'auth'], function () {    Route::get('/index/', 'IndexController@index');    Route::get('/goodslist/', 'GoodsController@index');    Route::get('/category/', 'CategoryController@index');    Route::get('/goods_detail/', 'GoodsController@detail');    Route::get('/addcart/', 'FlowController@addcart');    Route::get('/cart/', 'FlowController@cart');    Route::post('/flow/buy/', 'FlowController@buy');    Route::get('/flow/zj_buy/', 'FlowController@zj_buy');    Route::get('/flow/cart_buy/', 'FlowController@cart_buy');    Route::get('/integral/', 'GoodsController@integral');    Route::get('/integral_detail/', 'GoodsController@integral_detail');    Route::post('/flow/number/', 'FlowController@update_number');    Route::post('/flow/del/', 'FlowController@delete');    Route::post('/flow/zj_cart_buy/', 'FlowController@zj_cart_buy');    Route::post('/flow/flow/coupon/', 'FlowController@coupon');    Route::get('/search/', 'GoodsController@search');    });});